import React from 'react';
import renderPromise from '../otil/renderPromise.js'
import DishDetailsContainer from '../details/detailsContainer'
const h = React.createElement;



export default class SearchView extends React.Component{
    constructor(props){
        super(props);
        this.props = props;
        this.state = {

        };

    }

    update() {
        this.setState({
        })
      }

    componentDidMount() {
        this.updateSearchResults()
        this.props.model.addObserver(() => this.update());
      }
      // this is called when component is removed from the DOM
      // good place to remove observer
    componentWillUnmount() {
        this.props.model.removeObserver(this)
      }

    render(){

        return h("div", null,  
         h("div", null,
            h("input", {id:"searchInput"}, null),// free text search box
            h("select", {id:"selectInput"},  
            h("option", {value:""}, "Choose:"),                             // empty first choice
            // the rest of the SELECT children are generated from an array:
            ["starter", "main course", "dessert"].map(opt=> h("option", {value: opt}, opt))
            ),  

            h("button",{onClick: () => this.updateSearchResults()}, "Search!"), // search button

            h("button",{class:"navigationbutton", onClick:() =>{ 
            document.getElementById("search").classList.add('hide');
            document.getElementById("summary").classList.remove('hide');
            }}, "Summary" )), // end of search box

            h("div",{id:"searchresult"},null), // empty div for search results

        )
         // initially populate the resultDiv with nice dish images

    }



    createDishDisplay(dish){
        return h("span",{ class:"food", id: dish.id, title: dish.title ,onClick: () => {
            DishDetailsContainer.updateDishdisplay(dish.id)
            this.props.model.getDishDetails(dish.id).then(dish => this.props.model.addToMenu(dish))
        }//Addbutton
    }, h("span" ,null, dish.title), h("img", {src: "https://spoonacular.com/recipeImages/" + dish.image},null));
    }


    updateSearchResults(){

        renderPromise( 
            this.props.model.searchDishes(document.getElementById("selectInput").value, document.getElementById("searchInput").value), 
            dishes=> h("div", null, dishes.map(dish => this.createDishDisplay(dish))),
            document.getElementById("searchresult")
        )


    } 

    isDishRepresentation(clickedNode){


        if(clickedNode.className.includes("food") === true) {
            return clickedNode.getAttribute("id")

        }

        if( clickedNode.parentElement.className.includes("food") === true){  
                return clickedNode.parentElement.getAttribute("id")
        }

        else{

             return null
        }


    }


}
