import React from 'react';
import './App.css';
import dinnerModel from './dinnerstuff/DinnerModel.js'
import SidebarView from './sidebar/sidebarView';
import SearchView  from './search/searchView';
import SummaryView from './summary/summaryView';
import DishDetailsContainer from './details/detailsContainer'

const modelString= localStorage.getItem("dinnerModel");
let modelObject= JSON.parse(modelString);
if (modelObject === undefined){ modelObject = {}}
const summaryNav=[()=> show("#summary"), "Summary"];
const backToSearch=[()=> show("#search"), "Back to search"];
const addToMenu=[()=> show("#search"), "Add to menu"];
const details = new DishDetailsContainer(model, document.body.querySelector("#details"), addToMenu, backToSearch);
new SidebarController(model, document.body.querySelector("#sidebar"));
new SummaryController(model, document.body.querySelector("#summary"),backToSearch);
new SearchController(model, document.body.querySelector("#search"), summaryNav,
     id=> {
         details.render(id);    
         show("#details")
       });





if (!window.location.hash) {
   window.location.hash = 'search';
}
show(window.location.hash);
window.onhashchange = ()=> show(window.location.hash);




import {
  BrowserRouter as Router,
  Switch,
  Route,

} from "react-router-dom"


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
    }
  } 


  render(){
    return (
      <Router>
        <Switch>
          <div className="App flexParent ">
          <React.Fragment></React.Fragment>
            <div className = "sidebar debug">
            <SidebarView model={dinnerModel}/>
            </div>
            <React.Fragment></React.Fragment>
            <div className = "mainContent debug" id = "search">
              <Route path="/" render={() => <SearchView model={dinnerModel}/>}/>
            </div>
            <React.Fragment></React.Fragment>
            <div className = "mainContent hide debug" id="details">
              <Route path="/details/:Id" render={(props) => <DishDetailsContainer dishid={props.match.params.Id} model={dinnerModel}/>}/>
            </div>
            <React.Fragment></React.Fragment>
            <div className = "mainContent hide debug" id= "summary">
              <Route path="/summary" render={() => <SummaryView model={dinnerModel}/>}/>
            </div>
            <React.Fragment></React.Fragment>
          </div>
          </Switch>
      </Router>
    );

  }
}

export default App;

