import React from 'react';
import ReactDOM from 'react-dom';
const h = React.createElement;

export default function renderPromise(promise, hyperscript, node){
debugger;
    const spinner=createSpinner();
    spinner.ReactDOM.render(node);


    promise
    .then(result=> hyperscript(result).ReactDOM.render(node))
    .catch(err=> h("div", err, "ERROR").ReactDOM.render(node))
    //.finally(()=> spinner.remove);
}

function createSpinner(){
    return h("img", {src:'spinner.gif'})

}
