import React from 'react';
import {Link} from 'react-router-dom';
const h = React.createElement;


export default class SummaryView extends React.Component{
   constructor(props){
    super(props)
        this.props = props;
      this.state = {
      ingredients: this.props.model.getShoppingList(), 
      guests: this.props.model.getNumberOfGuests()
      }
       
   }
   
   update() {
        
    this.setState({
        ingredients: this.props.model.getShoppingList(), 
        guests: this.props.model.getNumberOfGuests()
    })
  }


componentDidMount() {
    this.props.model.addObserver(() => this.update());
  }

  // this is called when component is removed from the DOM
  // good place to remove observer
  componentWillUnmount() {
    this.props.model.removeObserver(this)
  }

render(){
    return h("div", null ,h("span" ,null,`Dinner for `+ this.state.guests + ` people`), 

        <Link to= '/'>
            <button className="navigationbutton" onClick={() =>{ 
                
                document.getElementById("summary").classList.add('hide');
                document.getElementById("search").classList.remove('hide');
                }}>
            Back to search
            </button>
        </Link>    
        ,
        h("table" ,{}, 
        h("tr" ,{}, h("th", null, " Ingredient"), h("th", null,"supermarket aisle"),h("th", null, "amount")),
        this.state.ingredients.map(ingredient=> h("tr",{}, h("td" ,null, ingredient.name), 
        h("td", null, ingredient.aisle) , h("td", null, ingredient.amount * this.state.guests))),
        h("tr",null, h("th",null, "Total Price:") , h("th" ,null, this.getTotalPrice(this.state.ingredients, this.state.guests)), h("th",null, "SEK"))
        ))}
    

    
getTotalPrice(ingredients, guests) {
    let list = ingredients;
    let price = 0;
    list.forEach(ingredient =>{ price = price + ingredient.amount});

    return price*guests
}
}
