// js/summary/summaryView.js
import React from 'react';
const h = React.createElement;


 

export default class SidebarView extends React.Component{
    constructor(model){
        super()
        this.model=model;
        model.addObserver(x=>this.update(x));
        this.menuprice = 0;
        this.dishtypesort= {"starter": 1 , "main course" : 2 , "dessert": 3 };
        
    }
    render(){
        this.menuprice = 0;
        return h("div", h("button", {onClick: e=> this.model.setNumberOfGuests(this.model.getNumberOfGuests() -1)}, "-"), h("span", this.model.getNumberOfGuests()), h("button", {onClick: e=> this.model.setNumberOfGuests(this.model.getNumberOfGuests() +1)}, "+"),
        )
        
    }

    getdishprice(dish){
        this.dishprice = sum(dish.extendedIngredients) * this.model.getNumberOfGuests();
        this.menuprice = this.menuprice + this.dishprice;
        return this.dishprice
    }
}



function sum(input){
             
    if (toString.call(input) !== "[object Array]")
       return false;
         
               var total =  0;
               for(var i=0;i<input.length;i++)
