import React from 'react';
import ReactDOM from 'react-dom';
import spin from './spinner.gif'

const h = React.createElement;

export default function RenderPromise(promise, hyperscript, node){
    const spinner=createSpinner();
    ReactDOM.render(spinner , node);                                                                          
    promise
    .then(result=> ReactDOM.render(hyperscript(result),node))
    .catch(err=> ReactDOM.render(h("div", err, "ERROR, Check connection and try again!"), node))
    .finally(()=> spinner.remove);
}

function createSpinner(){
    return h("img", {src:spin})

}
