// js/summary/summaryView.js
import React from 'react';
const h = React.createElement;

export default class SidebarView extends React.Component{
    constructor(model){
        super();
        this.model=model;
        this.menuprice = 0;
        this.dishtypesort= {"starter": 1 , "main course" : 2 , "dessert": 3 };
        
    }
    render(){
        this.menuprice = 0;
        return h("div",null,
        h("button", {onClick: e=> this.model.setNumberOfGuests(this.model.getNumberOfGuests()-1)}, "-"),
        h("span",null, this.model.getNumberOfGuests()),
        h("button", {onClick: e=> this.model.setNumberOfGuests(this.model.getNumberOfGuests()+1)}, "+"),
        h("div",null,
        h("table", null,
        h("th" ,null, h("td",null, "Dish name"), h("td", null,"Dish price (SEK)")),
            this.model.getMenu().sort(function(a, b){
            if(a.dishTypes.includes("starter")){return -1} 
            else if(a.dishTypes.includes("main course") && b.dishTypes.includes("dessert")){return -1} 
            else if(a.dishTypes.includes("dessert")){return 1}}
            ).map(dish=> h("tr",null, h("td" ,null, dish.title ), h("td",null,  this.getdishprice(dish)),h("button",{ onClick: event => {this.onDeleteClicked( dish.id)} , id: dish.id}, "delete"))),
        h("tr",null, h("td",null, "Total dinner price: "), h("td",null, this.menuprice))
        )));
        
    }

    getdishprice(dish){
        this.dishprice = sum(dish.extendedIngredients) * this.model.getNumberOfGuests();
        this.menuprice = this.menuprice + this.dishprice;
        return this.dishprice
    }
}



function sum(input){
             
    if (toString.call(input) !== "[object Array]")
       return false;
         
               var total =  0;
               for(var i=0;i<input.length;i++)
                 {                  
                   if(isNaN(input[i].amount)){
                   continue;
                    }
                     total += Number(input[i].amount);
                  }
                return total;
               }
