import React from 'react';
const h = React.createElement;


export default function DishDetailsView ({
    dish, 
    addControl:[addAction, addMessage], 
    onCancel:[cancelAction, cancelMessage], 
    price, 
    guests, 
    inMenu}){ 
    return h("div", null, h("p" ,null, "Name: "+ dish.title), h("p" ,null, "Type: ",  dish.dishTypes.map(dish => h("span",dish + ", " ))), h("p" , null,"Price: " +price), 
    h("img", {src: dish.image}), h("p" ,null, dish.instructions),
    h("div" ,null, inMenu ? h("button", {class:"navigationbutton", onClick: addAction, label:addMessage},"Add to menu"): h("span",null,"Dish is in menu!") ),
    h("div", null, h("button" , {class:"navigationbutton", onClick: cancelAction, label:cancelMessage},"Back to search")),
    h("table", null, 
    h("tr" ,null, h("th", null," Ingredient"), h("th",null, "amount")),
    dish.extendedIngredients.map(ingredient=> h("tr",null, h("td" ,null, ingredient.name), h("td", null, ingredient.amount * guests)))),
    h('a', {href: dish.sourceUrl}, 'CLICK HERE FOR MORE DISH DETAILS')
    )}
