import React from 'react';
import ReactDOM from 'react-dom';

const h = React.createElement;

export default function renderPromise(promise, hyperscript, node){
debugger;
    const spinner=createSpinner();
    ReactDOM.spinner.render(node);


    promise
    .then(result=> ReactDOM.createPortal(hyperscript(result),node))
    .catch(err=> h("div", err, "ERROR").render(node))
    //.finally(()=> spinner.remove);
}

function createSpinner(){
    return h("img", {src:'spinner.gif'})

}
