import React from 'react';
const h = React.createElement;

export default class SearchView extends React.Component{
    constructor(model ){

        this.model=model;
    }

    render(){
        this.updateSearchResults();

        return h("div",  
        this.root.innerHTML= h("div", null,
            this.textControl= h("input"), null, // free text search box
            this.typeControl= h("select",  
            h("option", {value:""}, "Choose:"),                             // empty first choice
            // the rest of the SELECT children are generated from an array:
            ["starter", "main course", "dessert"].map(opt=> h("option", opt))
            ),  

            h("button", "Search!"), // search button

            h("button",{class:"navigationbutton", onclick:this.doneCallback, label:this.doneMessage}, "Summary" )), // end of search box

        this.resultDiv= h("div") // empty div for search results
        )
         // initially populate the resultDiv with nice dish images

    }

    createSpinner(){
        return h("img", { class: spinner , src:"./search/spinner.gif"});
    }

    createDishDisplay(dish){
        return h("span",{ class:"food", id: dish.id, title: dish.title }, h("span", dish.title), h("img", {src: "https://spoonacular.com/recipeImages/" + dish.image}));
    }

    updateSearchResults(){
        renderPromise(
            this.model.searchDishes(this.typeControl.value, this.textControl.value), 
            dishes=> h("div", dishes.map(dish => this.createDishDisplay(dish))),
            this.resultDiv
        )


    } 

    isDishRepresentation(clickedNode){


        if(clickedNode.className.includes("food") == true) {
            return clickedNode.getAttribute("id")

        }

        if( clickedNode.parentElement.className.includes("food") == true){  
                return clickedNode.parentElement.getAttribute("id")
        }

        else{

             return null
        }


    }


}
