                guests: this.props.model.getNumberOfGuests(),
                inMenu: this.props.model.dishInMenu(dish)}));
        }


   render(id){  
     return renderPromise(this.model.getDishDetails(id), 
           dish => { return this.currentDish = dish, this.createDishDisplay(this.currentDish);},
           this.root)
  }
}
