import React from 'react';
import {Link} from 'react-router-dom';

const h = React.createElement;



export default function DishDetailsView ({
    dish, 
    addControl:[addAction, addMessage], 
    onCancel:[cancelAction, cancelMessage], 
    price, 
    guests, 
    inMenu}){ 
        console.log(dish.dishTypes);
        
    return h("div", {},
    h("p" ,{}, "Name: "+ dish.title), 
    <p> Type:  {dish.dishTypes.map(dish => <span>{dish}, </span>)}</p>, 
    h("p" , null,"Price: " + price), 
    h("img", {src: dish.image}), 
    h("p" ,null, dish.instructions),
    h("div" ,null, 
    inMenu?h("button", {class:"navigationbutton", onClick: addAction, label:addMessage},"Add to menu"): h("span",null,"Dish is in menu!")),
    h("div", null, 
    
    <Link to= '/search'>
            <button className="navigationbutton" onClick={() =>{ 
                document.getElementById("details").classList.add('hide')
                document.getElementById("search").classList.remove('hide')
                }}>
            Back to search
            </button>
    </Link>
  
    ),
    h("table", null, 
    h("tr" ,null, 
    h("th", null," Ingredient"), h("th",null, "amount")),
    dish.extendedIngredients.map(ingredient=> h("tr",null, h("td" ,null, ingredient.name), h("td", null, ingredient.amount * guests)))),
    h('a', {href: dish.sourceUrl}, 'CLICK HERE FOR MORE DISH DETAILS')
    )}
